def build(t, cat, eng):
    lo,hi = XP[t["tier"]]
    return {"id":t["id"],"category":cat,"tier":t["tier"],"title":t["title"],
            "xp":random.randint(lo,hi),"story":random.choice(STORIES[t["tier"]]),
            "description":t["desc"],"initial_code":t.get("init",""),
            "check_logic":{"engine":eng,"cases":t.get("cases",[])}}

with open("tasks.json") as f: data = json.load(f)
fe = [build(t,"frontend","html-css") for t in FE]
sc = [build(t,"scratch","manual") for t in SC]
data["tasks"].extend(fe + sc)
with open("tasks.json","w") as f: json.dump(data, f, ensure_ascii=False, indent=2)

cats={}; tiers={}
for t in data["tasks"]:
    c=t.get("category","?"); cats[c]=cats.get(c,0)+1
    tr=t.get("tier","?"); tiers[tr]=tiers.get(tr,0)+1
print(f"Added {len(fe)} Frontend + {len(sc)} Scratch. Total: {len(data['tasks'])}")
print(f"By category: {cats}")
print(f"By tier: {tiers}")
